function getClipboard(){var t=Windows.ApplicationModel.DataTransfer.Clipboard.getContent();if(t.contains(Windows.ApplicationModel.DataTransfer.StandardDataFormats.text)){var a=JSON.stringify(t.getTextAsync(Windows.ApplicationModel.DataTransfer.StandardDataFormats.text));$("section#show-clipboard #textarea").text(a)}else $("section#show-clipboard #textarea").text("Unable to show clipboard content")}